            'spectrum-FieldGroup-group',
            {
              'spectrum-FieldGroup-group--horizontal': orientation === 'horizontal'
            }
          )
        }>
        <RadioContext.Provider
          value={{
            isEmphasized,
            validationState,
            state
          }}>
          {children}
        </RadioContext.Provider>
      </div>
    </div>
  );
