import { useEffect } from "react";
import { useState } from "react";

function RoleCard(props) {

    const [hover, setHover] = useState({
        hover: false,
        load: false
    });

    useEffect(() => {
        document.querySelector('.visibility').classList.add('default')
    },)

    function setClass() {
        if (hover.hover) {
            return "shade";
        } else if (hover.load) {
            return "light";
        }
    }

    function visibility() {
        if (hover.hover) {
            return "desc fade-in"
        } else if (hover.load) {
            return "desc fade-out"
        }
    }

    return (
        <div>
            <div className="role-card">
                <div className="role-title">
                    <div className={setClass()} onMouseEnter={() => setHover({hover: true, load: true})} onMouseLeave={() => setHover({hover: false, load: true})}>
                        {props.data.title}
                    </div>
                </div>
                <div className={visibility()}>
                    {props.data.desc}
                </div>
            </div>
        </div>
    );
}



export const ProjRoles = (props) => {

    return (
        <div className=" project-roles container">
            <div className="row">
                {/* <div className="img">
                    <img src="../img/project_team.png" alt="project" />
                </div> */}
                <h1 style={{textAlign: 'center',color: '#6372ff', margin: '2em 1em 1em 1em'}}>Project roles</h1>
                <h3 >Learn more about each of the 3 roles every project has! 
                    Each category is coordinated with workshops that help
                    students develop production-level code. <span style={{'color':'#6372ff'}}> Hover over the buttons to learn more!</span>
                </h3>
                <RoleCard data={props.data.analysisRole}></RoleCard>
                <RoleCard data={props.data.platformRole}></RoleCard>
                <RoleCard data={props.data.datavizRole}></RoleCard>

            </div>
        </div>
    );
}
